<script>import DemoContainer from "../../shared/components/DemoContainer.svelte";
import { hoverAction } from "../..";
import Text from "../../shared/components/Text.svelte";
let text = "No!";
function handleHover(e) {
  text = e.detail.hover ? "Yes!" : "No!";
}
</script>

<DemoContainer>
	<div
		class="w-20 h-20 mb-4 rounded-lg bg-prime text-sm flex items-center justify-center text-white"
		use:hoverAction
		on:hover={handleHover}
	>
		Hover me
	</div>
	<Text>Mouse in the element: <span class="text-prime">{text}</span></Text>
</DemoContainer>
