<script>import { debounceClickAction, messagesStore } from "../..";
import DemoContainer from "../../shared/components/DemoContainer.svelte";
import { PrimaryButtonClassName } from "../../shared/tailwind";
function onClick() {
  messagesStore("Clicked!");
}
let duration = 1e3;
</script>

<DemoContainer>
	<label>
		<input type="range" bind:value={duration} max={2000} step={100} />
		{duration}ms
	</label>

	<div class="my-4">
		<button class={PrimaryButtonClassName} use:debounceClickAction={{ duration, onClick }}>
			Click will fire after {duration}ms
		</button>
	</div>
</DemoContainer>
