import { eventListenerStore } from "../../stores/eventListenerStore";
export function documentTitleAction(node) {
    let stop;
    const destroy = () => {
        stop && stop();
    };
    const update = () => {
        destroy();
        function handler() {
            document.title = node.value;
        }
        ({ stop } = eventListenerStore("input", handler, node));
    };
    update();
    return {
        update,
        destroy,
    };
}
