export function focusAction(node, value = true) {
    let stop;
    const destroy = () => {
        stop && stop();
    };
    const update = (value) => {
        destroy();
        if (value) {
            node.focus();
        }
        else {
            node.blur();
        }
    };
    update(value);
    return {
        update,
        destroy,
    };
}
