import { eventListenerStore } from "../../stores/eventListenerStore";
import { messagesStore } from "../../stores/messagesStore";
export function messageAction(node, params) {
    let stop;
    const destroy = () => {
        stop && stop();
    };
    const update = (params) => {
        destroy();
        function handleClick() {
            messagesStore(params.message, params.type);
        }
        ({ stop } = eventListenerStore("click", handleClick, node));
    };
    update(params);
    return {
        update,
        destroy,
    };
}
