import { eventListenerStore } from "../../stores/eventListenerStore";
import NotificationManager from "./NotificationManager";
export function notifyAction(node, params) {
    let stop;
    const destroy = () => {
        stop && stop();
    };
    const update = (params) => {
        destroy();
        function handleClick() {
            NotificationManager.getInstance().createNotification(params.title, params.description, params.type, params.duration);
        }
        ({ stop } = eventListenerStore("click", handleClick, node));
    };
    update(params);
    return {
        update,
        destroy,
    };
}
