<script>import { documentVisibilityStore, timeoutFnStore } from "../..";
import DemoContainer from "../../shared/components/DemoContainer.svelte";
import Text from "../../shared/components/Text.svelte";
const visibility = documentVisibilityStore();
const startMessage = "\u{1F4A1} Minimize the page or switch tab then return";
const welcomeMessage = "\u{1F389} Welcome back!";
const { start } = timeoutFnStore(() => {
  message = startMessage;
}, 3e3);
let message = startMessage;
$: {
  if ($visibility === "visible") {
    start();
  } else {
    message = welcomeMessage;
  }
}
</script>

<DemoContainer>
	<Text>
		{message}
	</Text>
</DemoContainer>
