<script>import { resizeObserverStore } from "../..";
import DemoContainer from "../../shared/components/DemoContainer.svelte";
let ref = null;
let width = 0;
let height = 0;
function handler([entry]) {
  width = entry.contentRect.width;
  height = entry.contentRect.height;
}
$:
  ref && resizeObserverStore(ref, handler);
$:
  stringify = `width: ${width}
height: ${height}`;
</script>

<DemoContainer>
	<p class="mb-4">Resize the box to see changes</p>
	<textarea
		bind:this={ref}
		bind:value={stringify}
		class="resize p-4 min-w-[100%] min-h-[100px] lg:min-w-[320px] lg:min-h-[100px] rounded-md"
	/>
</DemoContainer>
