<script>import { timeoutFnStore } from "../..";
import DemoContainer from "../../shared/components/DemoContainer.svelte";
import PrimaryButton from "../../shared/components/PrimaryButton.svelte";
const defaultText = "Please wait for 3 seconds";
let text = defaultText;
const { start, isPending } = timeoutFnStore(() => {
  text = "Fired!";
}, 3e3);
const restart = () => {
  text = defaultText;
  start();
};
</script>

<DemoContainer>
	<p class="mb-4">{text}</p>
	<PrimaryButton disabled={$isPending} on:click={restart}>Restart</PrimaryButton>
</DemoContainer>
