import { derived } from "svelte/store";
export function takeWhile(store, predicate) {
    return derived(store, ($store, set) => {
        if (typeof predicate === "function") {
            if (predicate($store)) {
                set($store);
            }
        }
        else {
            if (predicate) {
                set($store);
            }
        }
    });
}
