import { tryOnDestroy } from "../../shared";
export function watch(store, callback, params = {}) {
    const { immediate = false } = params;
    let times = 0;
    let oldVal;
    const unsub = store.subscribe((value) => {
        if (immediate && times === 0) {
            callback(value, oldVal);
        }
        else if (times > 0) {
            callback(value, oldVal);
        }
        oldVal = value;
        times++;
    });
    tryOnDestroy(unsub);
}
